f = function(n = 100) {
  sim = replicate(n, sample(1:15, 4, TRUE)) %&gt;%
    t() %&gt;% data.frame()
  sim$id = 1:n

  lvl = gather(sim, "stim", "lvl", X1:X4) %&gt;%
    count(lvl)

  list(
    min = min(lvl$n),
    max = max(lvl$n)
  )
}

df = map_df(1:1000, ~f())

