In script.js

&lt;script&gt;
function move_sidebar() {
    var w = window.innerWidth;
    if (w &lt; 992) {
      $("#toc").appendTo($("#main-nav"));
    } else {
      $("#toc").appendTo($("div.sidebar-chapter"));
    }
  }
  move_sidebar();
  window.onresize = move_sidebar;
&lt;/script&gt;

